configuration.config:
package Linker is

   Loader := external ("LOADER");

   Path_To_Gcc  := "${PATH(ada)}../lib/gcc/arm-eabi/4.9.3/";

   case CODE is
      when "arm"  => Path_To_Gcc := Path_To_Gcc & "arm/";
      when others =>
   end case;

   case FLOAT is
      when "hard" => Path_To_Gcc := Path_To_Gcc & "fpu/";
      when others =>
   end case;

   case ENDIAN is
      when "big-endian" => Path_To_Gcc := Path_To_Gcc & "be/";
      when others =>
   end case;

   for Required_Switches use Linker'Required_Switches &
     ("-L${RUNTIME_DIR(ada)}/adalib/", "-lgnat", "-lgnarl", "-T", Loader & ".ld") &
     ("-L" & Path_to_Gcc, "-nostdlib", "-lgcc");

   case Loader is

      when "flash" | "monitor" | "hiram" | "loram" =>
         for Required_Switches use Linker'Required_Switches &
            ("-Wl,-z,max-page-size="&MAX_PAGE_SIZE);

      when others =>

    end case;

end Linker;
