configuration.config:
package Compiler is

   Runtime_Adainclude := PROFILE_SUBPROFILE & "-" & FLOAT & "-" & CODE & "-" & CPU & "-" & FPU & "-" & IMPLEMENTATION & "/adainclude/";

   Common_Required_Switches := ("-m" & ENDIAN, "-m" & CODE, "-mfloat-abi=" & FLOAT, "-mcpu=" & CPU, "-mfpu=" & FPU);
   Common_Required_Switches := Common_Required_Switches & ("-I${RUNTIME_DIR(ada)}/../"&Runtime_Adainclude);

   Common_Required_Ada_Switches := ("-gnatQ", "-gnatw.X");
   Common_Required_C_Switches   := ();
   Common_Required_Asm_Switches := ();

   case BUILD is

      when "production" =>
         Common_Required_Ada_Switches := Common_Required_Ada_Switches & ("-O2");
         Common_Required_C_Switches   := Common_Required_C_Switches   & ("-O2");

      when "debug" =>
         Common_Required_Ada_Switches := Common_Required_Ada_Switches & ("-g", "-gnatwa", "-O0");
         Common_Required_C_Switches   := Common_Required_C_Switches   & ("-g", "-O0");
         Common_Required_Asm_Switches := Common_Required_Asm_Switches & ("-g");

      when others =>

   end case;

   for Required_Switches ("C")       use Compiler'Required_Switches ("C")       & Common_Required_Switches & Common_Required_C_Switches;
   for Required_Switches ("Ada")     use Compiler'Required_Switches ("Ada")     & Common_Required_Switches & Common_Required_Ada_Switches;
   for Required_Switches ("Asm_Cpp") use Compiler'Required_Switches ("Asm_Cpp") & Common_Required_Switches & Common_Required_Asm_Switches;

end Compiler;
